// export default RowWithColumns;

import React from "react";
import { Col, Row } from "react-bootstrap";

function RowWithColumns(props) {
  const { reverse } = props;
  return (
    <Row style={{ display: "flex", alignItems: "center" }}>
      {reverse ? (
        <>
        <Col xs={12} md={3} style={{ aspectRatio: 1 }}>
        <img
          src={props.img1}
          style={{ objectFit: "cover", width: "100%", height: "100%" }}
        />
      </Col>
      <Col xs={12} md={3} style={{ aspectRatio: 1 }}>
        <h1>Title</h1>
        <p>Description</p>
      </Col>
      <Col xs={12} md={3} style={{ aspectRatio: 1 }}>
        <img
          src={props.img2}
          style={{ objectFit: "cover", width: "100%", height: "100%" }}
        />
      </Col>
      <Col xs={12} md={3} style={{ aspectRatio: 1 }}>
        <h1>Title</h1>
        <p>Description</p>
      </Col>
        </>
      ) : (
        <>
        <Col xs={12} md={3} style={{ aspectRatio: 1 }}>
        <h1>Title</h1>
        <p>Description</p>
      </Col>
      <Col xs={12} md={3} style={{ aspectRatio: 1 }}>
        <img
          src={props.img1}
          style={{ objectFit: "cover", width: "100%", height: "100%" }}
        />
      </Col>
      <Col xs={12} md={3} style={{ aspectRatio: 1 }}>
        <h1>Title</h1>
        <p>Description</p>
      </Col>
      <Col xs={12} md={3} style={{ aspectRatio: 1 }}>
        <img
          src={props.img2}
          style={{ objectFit: "cover", width: "100%", height: "100%" }}
        />
      </Col>
        </>
      )}
    </Row>
  );
}

export default RowWithColumns;
