import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Card, CardImg, CardBody, CardTitle, CardText } from 'reactstrap';

function YelpReviews() {
  const [reviews, setReviews] = useState([]);

  // Fetch Yelp reviews on component mount
  useEffect(() => {
    fetch('http://localhost:5000/reviews', {
      headers: {
        Authorization: `Bearer 5ThobSqFHZkXwGg1Jv22u_BrIdA4lvcSwt9lSuqYxJoEQedOc29PS3kYus4VJzoOVZUiwQYQlBsD1cLFVUyQ--99Vven_Np-rLHT6hXk9eybrYCN4N1QeggxW4_RY3Yx`,
        'Content-Type': 'application/json',
      },
    })
      .then((response) => response.json())
      .then((data) => {
        setReviews(data.reviews.filter((review) => review.rating === 5));
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);

  return (
    <div className="container my-5">
      <h2 className="text-center">5-Star Yelp Reviews</h2>
      <div className="row row-cols-1 row-cols-md-3 g-4">
        {reviews.length > 0 ? (
          reviews.map((review) => (
            <div className="col" key={review.id}>
              <Card style={{ minHeight: '350px', maxHeight: '350px' }}>
                <CardImg top width="100%" src={review.user.image_url ? review.user.image_url : "https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460__340.png"} alt={review.user.name} />
                <CardBody>
                  <CardTitle tag="h5">{review.user.name}</CardTitle>
                  <CardText>{review.text}</CardText>
                </CardBody>
              </Card>
            </div>
          ))
        ) : (
          <p>Loading...</p>
        )}
      </div>
    </div>
  );
}

export default YelpReviews;
