import React from 'react';
import "./RowWithColumns.css"

const RowWithFourColumns = ({ title, description, imgSrc, imgAlt, img2Src, img2Alt, reverse }) => {
  const columns = [
    {
      title: title,
      description: description,
      imgSrc: imgSrc,
      imgAlt: imgAlt,
    },
    {
      title: "Column 2 Title",
      description: "Column 2 Description",
    },
    {
      title: "Column 3 Title",
      imgSrc: "./1.png",
      imgAlt: img2Alt,
    },
    {
      title: "Column 4 Title",
      description: "Column 4 Description",
    },
  ];

  if (reverse) {
    columns.reverse();
  }

  return (
    <div className="row-with-four-columns">
      {columns.map((column, index) => (
        <div key={index} className="column">
          {column.imgSrc && (
            <img src={column.imgSrc} alt={column.imgAlt} />
          )}
          <h2>{column.title}</h2>
          <p>{column.description}</p>
        </div>
      ))}
    </div>
  );
};

export default RowWithFourColumns;
