import React, { useState, useEffect } from "react";
import { FaArrowUp, FaArrowDown } from "react-icons/fa";
import "./ScrollToComponent.css";

const ScrollToComponent = ({ scrollToRef }) => {
  const [showComponent, setShowComponent] = useState(false);

  useEffect(() => {
    let timeoutId;

    const handleScroll = () => {
      setShowComponent(false);
      clearTimeout(timeoutId);
      timeoutId = setTimeout(() => {
        if (window.pageYOffset > 100 || window.pageYOffset === 0) {
          setShowComponent(true);
        } else {
          setShowComponent(false);
        }
      }, 500);
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  const scrollTo = () => {
    scrollToRef.current.scrollIntoView({ behavior: "smooth" });
  };

  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  return (
    <div className={`scroll-to-component ${showComponent ? "visible" : ""}`}>
      {showComponent && (
        <button className="back-to-top-button" onClick={scrollToTop}>
          <FaArrowUp className="arrow-icon" />
        </button>
      )}
      {showComponent && (
        <button className="scroll-to-button" onClick={scrollTo}>
          <FaArrowDown className="arrow-icon" />
        </button>
      )}
    </div>
  );
};

export default ScrollToComponent;
