import React, { useEffect } from 'react';
import AOS from 'aos';
import 'aos/dist/aos.css';

function Homepage() {
  useEffect(() => {
    AOS.init({
      duration: 1800,
      easing: 'ease',
    });
  }, []);

  const handleButtonClick = (event) => {
    const { className } = event.target;
    const navDiv = document.querySelector('nav div');
    switch (className) {
      case 'nav-button':
        navDiv.classList.add('show');
        break;
      case 'exit-menu':
        navDiv.classList.remove('show');
        break;
      case 'to-top':
        window.scrollTo({
          top: 0,
          behavior: 'smooth',
        });
        break;
      default:
        break;
    }
  };

  return (
    <div>
      <button className="nav-button" onClick={handleButtonClick}>
        Toggle Menu
      </button>
      <button className="exit-menu" onClick={handleButtonClick}>
        Exit Menu
      </button>
      <button className="to-top" onClick={handleButtonClick}>
        Back to Top
      </button>
      <nav>
        <div>Menu</div>
        {/* Other elements */}
      </nav>
      {/* Other elements */}
    </div>
  );
}

export default Homepage;
